% directory within each subjects folder containing the input data
firstlevel_dir = 'func/'

% directory where all subjects folder are located in
base_dir = '/data/hippocampus/01_Enh_NKI/01_Data_NKI_fMRT/001_EnhNKI_17-60_N312/'

nrun = length(contrasts)

jobfile = {'sl_ADHD_contr_Age_Sex_Hand_IQ_FD_NKI_KH_job.m'}; % name of job-file with all further specifications (located in same folder)

jobs = repmat(jobfile, 1, nrun);
inputs = cell(3, nrun);

% creates automatically the following folder within the base_dir
for crun = 1:nrun    
    directory = { [base_dir '2ndLevel_ADHD_contr_Age_Sex_Hand_FSIQ_FD_exFD/within_module_degree_PC_uw_10-30_cov_ADHD_contr_Age_Sex_Hand_FSIQ_FD/' contrasts{crun} ]}
    mkdir(directory{1})
    
    con_list = {}
    for i = 1:length(subjects)
        con_list = [con_list sprintf( [base_dir subjects{1,i} filesep firstlevel_dir 'participation_coefficient_thresholdaverage_by_fslmaths_0.10-0.30_after_up3x3x3.nii,1'])]; % name of input data
    end
    
    inputs{1, crun} = directory;    
    inputs{2, crun} = con_list;     
    inputs{3, crun} = Sex;          
    inputs{4, crun} = ADHD;
    inputs{5, crun} = Age;
    inputs{6, crun} = Hand;
    inputs{7, crun} = WAIS_FULL;
    inputs{8, crun} = FD;
    inputs{9, crun} = contrasts{crun}; 

end
spm('defaults', 'FMRI');
spm_jobman('serial', jobs, '', inputs{:});
